package ex_01;

public class day_06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = -100;
		x = -x;
		System.out.println("x : " + x);
		
		byte b = 100;
		int y = -b;
		System.out.println("y : " + y);
		
		int m = 1; 
		
		int n = 1;
		int result1 = ++m + 10;
		int result2 = n++ + 10;
		
		System.out.println("result1의 값 : " + result1);
		System.out.println("result2의 값 : " + result2);
				
				
	}

}
